import "./style.css";
import { useState } from "react";

function App() {
  const [posts, setPosts] = useState([]);
  return (
    <>
      <header className="header">
        <div className="logo">
          <img src="logo.png" height="90" width="180" alt="GaelGoal Logo" />
        </div>
      </header>

      <NewPostForm setPosts={setPosts} />

      <main class="main">
        <TeamFilter />
        <PostList />
      </main>
    </>
  );
}

function NewPostForm({ setPosts }) {
  const [text, setText] = useState("");
  const [source, setSource] = useState("");
  const [team, setTeam] = useState("");
  const textLength = text.length;

  function handleSubmit(e) {
    e.preventDefault();
    console.log(text, source, team);

    const newPost = {
      id: Math.round(Math.random() * 100000),
      text,
      source,
      team,
      upvote: 0,
      createdIn: new Date().getFullYear(),
    };

    setPosts();
  }

  return (
    <form className="fact-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="What is your update?"
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <span>{200 - text.length}</span>
      <input
        type="text"
        placeholder="Fact source URL?"
        value={source}
        onChange={(e) => setSource(e.target.value)}
      />
      <select value={team} onChange={(e) => setTeam(e.target.value)}>
        <option value="">Choose club team:</option>
        <option value="Arsenal">Arsenal</option>
        <option value="Shelbourne">Shelbourne</option>
        <option value="Leeds">Leeds</option>
        <option value="Manchester United">Manchester United</option>
        <option value="Celtic">Celtic</option>
        <option value="Liverpool">Liverpool</option>
        <option value="Bohemians">Bohemians</option>
      </select>
      <button className="post">Post</button>
    </form>
  );
}

function TeamFilter() {
  return (
    <aside>
      <ul>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Arsenal
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Shelbourne
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Leeds
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Manchester United
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Celtic
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Liverpool
          </button>
        </li>
        <li className="team">
          <button
            className="btn btn-team"
            style={{ backgroundColor: "#ffffff" }}
          >
            Bohemians
          </button>
        </li>
      </ul>
    </aside>
  );
}

function PostList() {
  const [posts, setPosts] = useState([]);
  return <section>Post List</section>;
}

export default App;
